mapper.select:
select

from address a
where a.id = #{id}

mapper.update:
UPDATE address a
a.uid = #{vo.uid}, a.province_id = #{vo.provinceId}, a.city_id = #{vo.cityId}, a.district_id = #{vo.districtId}, a.link_man = #{vo.linkMan}, a.address = #{vo.address}, a.mobile = #{vo.mobile}, a.code = #{vo.code}, a.is_default = #{vo.isDefault}, a.status = #{vo.status}, a.update_time = now()
WHERE a.id = #{vo.id}
