mapper.select:
select

from t_sys_user a
where a.id = #{id}

mapper.insert:
 insert into t_sys_user(f_account, f_role, f_password, f_real_name, f_salt, f_status, f_create_time) 
	 values
 (#{vo.account},#{vo.role},#{vo.password},#{vo.realName},#{vo.salt},#{vo.status},now());

mapper.update:
UPDATE t_sys_user a
a.f_account = #{vo.account}, a.f_password = #{vo.password}, a.f_real_name = #{vo.realName}, a.f_salt = #{vo.salt}, a.f_token = #{vo.token}, a.f_status = #{vo.status}, a.f_login_ip = #{vo.loginIp}, a.f_create_time = #{vo.createTime},
WHERE a.id = #{vo.id}
