mapper.select:
select

from t_sys_user a
where a.id = #{id}

mapper.insert:
 insert into t_sys_user(account, role, password, real_name, salt, status, create_time) 
	 values
 (#{vo.account},#{vo.role},#{vo.password},#{vo.realName},#{vo.salt},#{vo.status},now());

mapper.update:
UPDATE t_sys_user a
a.account = #{vo.account}, a.password = #{vo.password}, a.real_name = #{vo.realName}, a.salt = #{vo.salt}, a.token = #{vo.token}, a.status = #{vo.status}, a.login_ip = #{vo.loginIp}, a.create_time = #{vo.createTime},
WHERE a.id = #{vo.id}
